import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import toast from 'react-hot-toast';
import './Auth.css';

const Register = () => {
    const [username, setUsername] = useState('');
    const [phoneValue, setPhoneValue] = useState('+998 ');
    const [usernameError, setUsernameError] = useState('');
    const [phoneError, setPhoneError] = useState('');
    const { register: registerUser, loading, error, isAuthenticated, clearError } = useAuth();
    const navigate = useNavigate();

    useEffect(() => {
        if (isAuthenticated) {
            navigate('/chat');
        }
    }, [isAuthenticated, navigate]);

    useEffect(() => {
        if (error) {
            toast.error(error);
            clearError();
        }
    }, [error, clearError]);

    const onSubmit = async (e) => {
        e.preventDefault();

        // Validate username
        if (!username.trim()) {
            setUsernameError('Username is required');
            return;
        }
        if (username.length < 3) {
            setUsernameError('Username must be at least 3 characters');
            return;
        }
        if (username.length > 20) {
            setUsernameError('Username cannot exceed 20 characters');
            return;
        }
        if (!/^[a-zA-Z0-9_]+$/.test(username)) {
            setUsernameError('Username can only contain letters, numbers, and underscores');
            return;
        }

        // Validate phone number
        const phoneRegex = /^\+998\s\d{2}\s\d{3}\s\d{2}\s\d{2}$/;
        if (!phoneRegex.test(phoneValue)) {
            setPhoneError('Please enter a valid Uzbek phone number');
            return;
        }

        setUsernameError('');
        setPhoneError('');

        const result = await registerUser({
            username: username.trim(),
            phone: phoneValue
        });

        if (result.success) {
            toast.success('Registration successful!');
            navigate('/chat');
        }
    };

    const formatPhoneNumber = (value) => {
        // Remove all non-digits
        const phoneNumber = value.replace(/\D/g, '');

        // Always start with 998
        let formatted = '+998 ';

        if (phoneNumber.length > 3) {
            const remaining = phoneNumber.slice(3);

            if (remaining.length >= 2) {
                formatted += remaining.slice(0, 2);
            }
            if (remaining.length >= 5) {
                formatted += ' ' + remaining.slice(2, 5);
            }
            if (remaining.length >= 7) {
                formatted += ' ' + remaining.slice(5, 7);
            }
            if (remaining.length >= 9) {
                formatted += ' ' + remaining.slice(7, 9);
            }
        }

        return formatted;
    };

    const handleUsernameChange = (e) => {
        setUsername(e.target.value);
        setUsernameError('');
    };

    const handlePhoneChange = (e) => {
        const inputValue = e.target.value;

        // Don't allow deletion of +998 prefix
        if (inputValue.length < 5) {
            setPhoneValue('+998 ');
            return;
        }

        const formatted = formatPhoneNumber(inputValue);
        setPhoneValue(formatted);
        setPhoneError('');
    };

    const handleKeyDown = (e) => {
        // Prevent deletion of +998 prefix
        if ((e.key === 'Backspace' || e.key === 'Delete') && phoneValue.length <= 5) {
            e.preventDefault();
        }
    };

    const handleFocus = (e) => {
        // Set cursor position after +998 
        setTimeout(() => {
            e.target.setSelectionRange(5, 5);
        }, 0);
    };

    return (
        <div className="auth-container">
            <div className="auth-background">
                <div className="auth-card">
                    <div className="auth-header">
                        <div className="auth-logo">
                            <div className="logo-icon">
                                <svg viewBox="0 0 24 24" fill="currentColor">
                                    <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm-2 15l-5-5 1.41-1.41L10 14.17l7.59-7.59L19 8l-9 9z" />
                                </svg>
                            </div>
                            <h1>Telegram Clone</h1>
                        </div>
                        <p className="auth-subtitle">Create your account to start chatting!</p>
                    </div>

                    <form className="auth-form" onSubmit={onSubmit}>
                        <div className="form-group">
                            <label htmlFor="username">Username</label>
                            <div className="input-wrapper">
                                <input
                                    type="text"
                                    id="username"
                                    className={`form-input ${usernameError ? 'error' : ''}`}
                                    placeholder="Choose a username"
                                    value={username}
                                    onChange={handleUsernameChange}
                                    autoComplete="username"
                                />
                                <div className="input-icon">
                                    <svg viewBox="0 0 24 24" fill="currentColor">
                                        <path d="M12 12c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm0 2c-2.67 0-8 1.34-8 4v2h16v-2c0-2.66-5.33-4-8-4z" />
                                    </svg>
                                </div>
                            </div>
                            {usernameError && <span className="error-message">{usernameError}</span>}
                        </div>

                        <div className="form-group">
                            <label htmlFor="phone">Phone Number</label>
                            <div className="input-wrapper">
                                <input
                                    type="tel"
                                    id="phone"
                                    className={`form-input ${phoneError ? 'error' : ''}`}
                                    placeholder="+998 XX XXX XX XX"
                                    value={phoneValue}
                                    onChange={handlePhoneChange}
                                    onKeyDown={handleKeyDown}
                                    onFocus={handleFocus}
                                    maxLength={17}
                                    autoComplete="tel"
                                />
                                <div className="input-icon">
                                    <svg viewBox="0 0 24 24" fill="currentColor">
                                        <path d="M6.62 10.79c1.44 2.83 3.76 5.14 6.59 6.59l2.2-2.2c.27-.27.67-.36 1.02-.24 1.12.37 2.33.57 3.57.57.55 0 1 .45 1 1V20c0 .55-.45 1-1 1-9.39 0-17-7.61-17-17 0-.55.45-1 1-1h3.5c.55 0 1 .45 1 1 0 1.25.2 2.45.57 3.57.11.35.03.74-.25 1.02l-2.2 2.2z" />
                                    </svg>
                                </div>
                            </div>
                            {phoneError && <span className="error-message">{phoneError}</span>}
                        </div>

                        <div className="phone-info">
                            <p>Your phone number will be your unique identifier</p>
                        </div>

                        <button
                            type="submit"
                            className={`auth-button ${loading ? 'loading' : ''}`}
                            disabled={loading || !username.trim() || phoneValue.length < 17}
                        >
                            {loading ? (
                                <>
                                    <div className="spinner"></div>
                                    Creating account...
                                </>
                            ) : (
                                'Create Account'
                            )}
                        </button>
                    </form>

                    <div className="auth-footer">
                        <p>
                            Already have an account?{' '}
                            <Link to="/login" className="auth-link">
                                Sign in here
                            </Link>
                        </p>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Register;
