import React, { useState, useEffect, useRef } from 'react';
import { usersAPI } from '../../services/api';
import toast from 'react-hot-toast';
import './Search.css';

const Search = ({ onClose, onSelectUser }) => {
    const [query, setQuery] = useState('');
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const searchTimeoutRef = useRef(null);
    const inputRef = useRef(null);

    useEffect(() => {
        // Focus input when component mounts
        if (inputRef.current) {
            inputRef.current.focus();
        }
    }, []);

    useEffect(() => {
        // Clear previous timeout
        if (searchTimeoutRef.current) {
            clearTimeout(searchTimeoutRef.current);
        }

        // If query is empty or too short, clear results
        if (!query.trim() || query.trim().length < 2) {
            setUsers([]);
            setError('');
            return;
        }

        // Set loading state
        setLoading(true);
        setError('');

        // Debounce search
        searchTimeoutRef.current = setTimeout(async () => {
            try {
                const response = await usersAPI.search(query.trim());
                if (response.data.success) {
                    setUsers(response.data.users);
                    if (response.data.users.length === 0) {
                        setError('No users found');
                    }
                }
            } catch (error) {
                console.error('Search error:', error);
                setError('Failed to search users');
                toast.error('Search failed');
            } finally {
                setLoading(false);
            }
        }, 300); // 300ms debounce

        return () => {
            if (searchTimeoutRef.current) {
                clearTimeout(searchTimeoutRef.current);
            }
        };
    }, [query]);

    const handleUserSelect = (user) => {
        onSelectUser(user);
        onClose();
    };

    const formatLastSeen = (lastSeen) => {
        const now = new Date();
        const lastSeenDate = new Date(lastSeen);
        const diffInMinutes = Math.floor((now - lastSeenDate) / (1000 * 60));

        if (diffInMinutes < 1) return 'Just now';
        if (diffInMinutes < 60) return `${diffInMinutes}m ago`;

        const diffInHours = Math.floor(diffInMinutes / 60);
        if (diffInHours < 24) return `${diffInHours}h ago`;

        const diffInDays = Math.floor(diffInHours / 24);
        if (diffInDays < 7) return `${diffInDays}d ago`;

        return lastSeenDate.toLocaleDateString();
    };

    const getDisplayName = (user) => {
        const fullName = `${user.firstName} ${user.lastName}`.trim();
        return fullName || user.username;
    };

    return (
        <div className="search-overlay">
            <div className="search-modal">
                <div className="search-header">
                    <h2>Search Users</h2>
                    <button className="close-button" onClick={onClose}>
                        <svg viewBox="0 0 24 24" fill="currentColor">
                            <path d="M19 6.41L17.59 5 12 10.59 6.41 5 5 6.41 10.59 12 5 17.59 6.41 19 12 13.41 17.59 19 19 17.59 13.41 12z" />
                        </svg>
                    </button>
                </div>

                <div className="search-content">
                    <div className="search-input-container">
                        <div className="search-input-wrapper">
                            <svg className="search-icon" viewBox="0 0 24 24" fill="currentColor">
                                <path d="M15.5 14h-.79l-.28-.27C15.41 12.59 16 11.11 16 9.5 16 5.91 13.09 3 9.5 3S3 5.91 3 9.5 5.91 16 9.5 16c1.61 0 3.09-.59 4.23-1.57l.27.28v.79l5 4.99L20.49 19l-4.99-5zm-6 0C7.01 14 5 11.99 5 9.5S7.01 5 9.5 5 14 7.01 14 9.5 11.99 14 9.5 14z" />
                            </svg>
                            <input
                                ref={inputRef}
                                type="text"
                                className="search-input"
                                placeholder="Search by username, name, or phone..."
                                value={query}
                                onChange={(e) => setQuery(e.target.value)}
                            />
                            {query && (
                                <button
                                    className="clear-button"
                                    onClick={() => setQuery('')}
                                >
                                    <svg viewBox="0 0 24 24" fill="currentColor">
                                        <path d="M19 6.41L17.59 5 12 10.59 6.41 5 5 6.41 10.59 12 5 17.59 6.41 19 12 13.41 17.59 19 19 17.59 13.41 12z" />
                                    </svg>
                                </button>
                            )}
                        </div>
                    </div>

                    <div className="search-results">
                        {loading && (
                            <div className="search-loading">
                                <div className="spinner"></div>
                                <p>Searching...</p>
                            </div>
                        )}

                        {error && !loading && (
                            <div className="search-error">
                                <div className="error-icon">üîç</div>
                                <p>{error}</p>
                            </div>
                        )}

                        {!loading && !error && query.trim().length < 2 && (
                            <div className="search-placeholder">
                                <div className="placeholder-icon">üë•</div>
                                <h3>Search for users</h3>
                                <p>Enter at least 2 characters to start searching</p>
                            </div>
                        )}

                        {!loading && !error && users.length > 0 && (
                            <div className="users-list">
                                {users.map(user => (
                                    <div
                                        key={user.id}
                                        className="user-item"
                                        onClick={() => handleUserSelect(user)}
                                    >
                                        <div className="user-avatar">
                                            <img src={user.avatar} alt={user.username} />
                                            <div className={`status-indicator ${user.isOnline ? 'online' : 'offline'}`}></div>
                                        </div>

                                        <div className="user-info">
                                            <div className="user-name">
                                                <h4>{getDisplayName(user)}</h4>
                                                {user.firstName && user.lastName && (
                                                    <span className="username">@{user.username}</span>
                                                )}
                                            </div>
                                            <div className="user-status">
                                                {user.isOnline ? (
                                                    <span className="online-status">Online</span>
                                                ) : (
                                                    <span className="offline-status">
                                                        Last seen {formatLastSeen(user.lastSeen)}
                                                    </span>
                                                )}
                                            </div>
                                        </div>

                                        <div className="user-actions">
                                            <svg viewBox="0 0 24 24" fill="currentColor">
                                                <path d="M8.59 16.59L13.17 12 8.59 7.41 10 6l6 6-6 6-1.41-1.41z" />
                                            </svg>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Search;
