import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import { useAuth } from '../../context/AuthContext';
import { messagesAPI } from '../../services/api';
import toast from 'react-hot-toast';
import Profile from '../Profile/Profile';
import Search from '../Search/Search';
import './Chat.css';

const Chat = () => {
    const { user, token, logout } = useAuth();
    const [socket, setSocket] = useState(null);
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');
    const [onlineUsers, setOnlineUsers] = useState([]);
    const [typingUsers, setTypingUsers] = useState(new Map());
    const [isTyping, setIsTyping] = useState(false);
    const [connected, setConnected] = useState(false);
    const [connectionStatus, setConnectionStatus] = useState('Connecting...');
    const [showProfile, setShowProfile] = useState(false);
    const [showSearch, setShowSearch] = useState(false);
    const [lastActivity, setLastActivity] = useState(new Date());
    const [currentChatId, setCurrentChatId] = useState('general');

    const messagesEndRef = useRef(null);
    const typingTimeoutRef = useRef(null);
    const messagesContainerRef = useRef(null);

    useEffect(() => {
        if (user && token) {
            const newSocket = io('http://localhost:5005', {
                auth: {
                    token: token
                },
                transports: ['websocket', 'polling']
            });

            newSocket.on('connect', () => {
                console.log('âœ… Connected to server');
                setConnected(true);
                setConnectionStatus('Connected');
                toast.success('Connected to chat!', {
                    icon: 'ðŸŸ¢',
                    duration: 2000
                });
            });

            newSocket.on('disconnect', (reason) => {
                console.log('âŒ Disconnected from server:', reason);
                setConnected(false);
                setConnectionStatus('Disconnected');
                setTypingUsers(new Map());
                toast.error('Disconnected from chat', {
                    icon: 'ðŸ”´',
                    duration: 3000
                });
            });

            newSocket.on('connect_error', (error) => {
                console.error('Connection error:', error);
                setConnectionStatus('Connection failed');
                toast.error('Failed to connect to chat');
            });

            newSocket.on('user-joined', (userData) => {
                console.log('ðŸ‘‹ User joined:', userData);
                setLastActivity(new Date());
            });

            newSocket.on('message-history', (history) => {
                setMessages(history);
                setLastActivity(new Date());
            });

            newSocket.on('new-message', (message) => {
                setMessages(prev => [...prev, message]);
                setLastActivity(new Date());
            });

            newSocket.on('online-users', (users) => {
                setOnlineUsers(users);
                setLastActivity(new Date());
            });

            newSocket.on('user-online', (userData) => {
                toast.success(`${userData.username} joined the chat`, {
                    icon: 'ðŸ‘‹',
                    duration: 3000
                });
                setLastActivity(new Date());
            });

            newSocket.on('user-offline', (userData) => {
                toast(`${userData.username} left the chat`, {
                    icon: 'ðŸ‘‹',
                    duration: 3000
                });

                setTypingUsers(prev => {
                    const newMap = new Map(prev);
                    newMap.delete(userData.id);
                    return newMap;
                });
                setLastActivity(new Date());
            });

            newSocket.on('user-typing', (data) => {
                setTypingUsers(prev => {
                    const newMap = new Map(prev);
                    if (data.isTyping) {
                        newMap.set(data.user.id, {
                            ...data.user,
                            timestamp: new Date()
                        });
                    } else {
                        newMap.delete(data.user.id);
                    }
                    return newMap;
                });
                setLastActivity(new Date());
            });

            newSocket.on('message-error', (error) => {
                toast.error(error.message, {
                    icon: 'âŒ',
                    duration: 4000
                });
            });

            setSocket(newSocket);

            return () => {
                newSocket.close();
            };
        }
    }, [user, token]);

    useEffect(() => {
        scrollToBottom();
    }, [messages]);

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    };

    const handleSendMessage = async (e) => {
        e.preventDefault();
        if (newMessage.trim() && socket && connected) {
            const messageText = newMessage.trim();
            setNewMessage('');
            handleStopTyping();

            socket.emit('send-message', {
                text: messageText,
                chatId: currentChatId
            });
        }
    };

    const handleTyping = (e) => {
        setNewMessage(e.target.value);

        if (!isTyping && socket && connected && e.target.value.trim()) {
            setIsTyping(true);
            socket.emit('typing', {
                isTyping: true,
                chatId: currentChatId
            });
        }

        clearTimeout(typingTimeoutRef.current);
        typingTimeoutRef.current = setTimeout(() => {
            handleStopTyping();
        }, 1500);
    };

    const handleStopTyping = () => {
        if (isTyping && socket && connected) {
            setIsTyping(false);
            socket.emit('typing', {
                isTyping: false,
                chatId: currentChatId
            });
        }
        clearTimeout(typingTimeoutRef.current);
    };

    const handleLogout = async () => {
        if (socket) {
            socket.close();
        }
        await logout();
        toast.success('Logged out successfully');
    };

    const handleUserSelect = (selectedUser) => {
        toast.success(`Starting chat with ${selectedUser.username}`);
    };

    const formatTime = (timestamp) => {
        return new Date(timestamp).toLocaleTimeString('en-US', {
            hour: '2-digit',
            minute: '2-digit'
        });
    };

    if (!user) {
        return (
            <div className="loading-container">
                <div className="spinner"></div>
                <p>Loading user data...</p>
            </div>
        );
    }

    return (
        <div className="chat-container">
            {/* LEFT PANEL - Chat List */}
            <div className="left-panel">
                <div className="left-panel-header">
                    <div className="user-profile-section" onClick={() => setShowProfile(true)}>
                        <img src={user.avatar} alt={user.username} className="user-avatar" />
                        <div className="user-info">
                            <h3>{user.username}</h3>
                            <div className={`user-status ${connected ? 'online' : 'offline'}`}>
                                {connected ? 'Online' : 'Offline'}
                            </div>
                        </div>
                    </div>
                    <div className="header-actions">
                        <button
                            className="header-btn"
                            onClick={() => setShowSearch(true)}
                            title="Search Users"
                        >
                            <svg viewBox="0 0 24 24" fill="currentColor">
                                <path d="M15.5 14h-.79l-.28-.27C15.41 12.59 16 11.11 16 9.5 16 5.91 13.09 3 9.5 3S3 5.91 3 9.5 5.91 16 9.5 16c1.61 0 3.09-.59 4.23-1.57l.27.28v.79l5 4.99L20.49 19l-4.99-5zm-6 0C7.01 14 5 11.99 5 9.5S7.01 5 9.5 5 14 7.01 14 9.5 11.99 14 9.5 14z" />
                            </svg>
                        </button>
                        <button className="header-btn" onClick={handleLogout} title="Logout">
                            <svg viewBox="0 0 24 24" fill="currentColor">
                                <path d="M17 7l-1.41 1.41L18.17 11H8v2h10.17l-2.58 2.59L17 17l5-5zM4 5h8V3H4c-1.1 0-2 .9-2 2v14c0 1.1.9 2 2 2h8v-2H4V5z" />
                            </svg>
                        </button>
                    </div>
                </div>

                <div className="search-bar">
                    <input
                        type="text"
                        className="search-input"
                        placeholder="Search chats..."
                    />
                </div>

                <div className="chat-list">
                    {/* General Chat */}
                    <div className={`chat-item ${currentChatId === 'general' ? 'active' : ''} online`}>
                        <img
                            src="https://ui-avatars.com/api/?name=General&background=0088cc&color=fff&size=96"
                            alt="General Chat"
                            className="chat-avatar"
                        />
                        <div className="chat-content">
                            <div className="chat-header">
                                <span className="chat-name">General Chat</span>
                                <span className="chat-time">
                                    {messages.length > 0 ? formatTime(messages[messages.length - 1].timestamp) : 'now'}
                                </span>
                            </div>
                            <div className="last-message">
                                {messages.length > 0
                                    ? `${messages[messages.length - 1].user.username}: ${messages[messages.length - 1].text.substring(0, 25)}${messages[messages.length - 1].text.length > 25 ? '...' : ''}`
                                    : 'No messages yet'
                                }
                            </div>
                        </div>
                        {Array.from(typingUsers.values()).length > 0 && (
                            <div className="unread-badge">
                                {Array.from(typingUsers.values()).length}
                            </div>
                        )}
                    </div>

                    {/* Online Users as Chats */}
                    {onlineUsers.filter(u => u.id !== user.id).map(onlineUser => (
                        <div key={onlineUser.id} className="chat-item online">
                            <img src={onlineUser.avatar} alt={onlineUser.username} className="chat-avatar" />
                            <div className="chat-content">
                                <div className="chat-header">
                                    <span className="chat-name">{onlineUser.username}</span>
                                    <span className="chat-time">online</span>
                                </div>
                                <div className="last-message">Online</div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>

            {/* RIGHT PANEL - Chat Messages */}
            <div className="right-panel">
                <div className="chat-header-right">
                    <div className="chat-info">
                        <img
                            src="https://ui-avatars.com/api/?name=General&background=0088cc&color=fff&size=84"
                            alt="General Chat"
                            className="chat-info-avatar"
                        />
                        <div className="chat-details">
                            <h2>General Chat</h2>
                            <div className={`chat-status ${connected ? 'online' : 'offline'}`}>
                                {onlineUsers.length} members, {onlineUsers.length} online
                            </div>
                        </div>
                    </div>
                    <div className="chat-actions">
                        <div className={`connection-status ${connected ? 'connected' : 'disconnected'}`}>
                            <div className="status-dot"></div>
                            <span className="status-text">{connectionStatus}</span>
                        </div>
                    </div>
                </div>

                <div className="messages-container" ref={messagesContainerRef}>
                    {messages.length === 0 ? (
                        <div className="empty-chat">
                            <div className="empty-icon">ðŸ’¬</div>
                            <h3>Welcome to General Chat!</h3>
                            <p>Start a conversation by sending a message below. Connect with {onlineUsers.length} online members.</p>
                        </div>
                    ) : (
                        messages.map(message => (
                            <div
                                key={message.id || message._id}
                                className={`message ${message.user.id === user.id ? 'own' : ''}`}
                            >
                                <img
                                    src={message.user.avatar}
                                    alt={message.user.username}
                                    className="message-avatar"
                                />
                                <div className="message-bubble">
                                    <div className="message-content">
                                        <div className="message-header">
                                            <span className="message-username">{message.user.username}</span>
                                            <span className="message-time">{formatTime(message.timestamp)}</span>
                                        </div>
                                        <div className="message-text">{message.text}</div>
                                    </div>
                                </div>
                            </div>
                        ))
                    )}

                    <div ref={messagesEndRef} />
                </div>

                {Array.from(typingUsers.values()).length > 0 && (
                    <div className="typing-indicator">
                        <div className="typing-avatars">
                            {Array.from(typingUsers.values()).slice(0, 3).map(user => (
                                <img
                                    key={user.id}
                                    src={user.avatar}
                                    alt={user.username}
                                    className="typing-avatar"
                                    title={`${user.username} is typing...`}
                                />
                            ))}
                        </div>
                        <div className="typing-text">
                            <span>
                                {Array.from(typingUsers.values()).length === 1
                                    ? `${Array.from(typingUsers.values())[0].username} is typing`
                                    : Array.from(typingUsers.values()).length === 2
                                        ? `${Array.from(typingUsers.values())[0].username} and ${Array.from(typingUsers.values())[1].username} are typing`
                                        : `${Array.from(typingUsers.values()).length} people are typing`
                                }
                            </span>
                            <div className="typing-dots">
                                <div className="typing-dot"></div>
                                <div className="typing-dot"></div>
                                <div className="typing-dot"></div>
                            </div>
                        </div>
                    </div>
                )}

                <div className="message-input-container">
                    <form className="message-input-form" onSubmit={handleSendMessage}>
                        <textarea
                            className="message-input"
                            placeholder={connected ? "Type a message..." : "Connecting..."}
                            value={newMessage}
                            onChange={handleTyping}
                            onBlur={handleStopTyping}
                            disabled={!connected}
                            rows={1}
                            onKeyDown={(e) => {
                                if (e.key === 'Enter' && !e.shiftKey) {
                                    e.preventDefault();
                                    handleSendMessage(e);
                                }
                            }}
                        />
                        <button
                            type="submit"
                            className="send-button"
                            disabled={!newMessage.trim() || !connected}
                        >
                            <svg viewBox="0 0 24 24" fill="currentColor">
                                <path d="M2.01 21L23 12 2.01 3 2 10l15 2-15 2z" />
                            </svg>
                        </button>
                    </form>
                </div>
            </div>

            {showProfile && (
                <Profile onClose={() => setShowProfile(false)} />
            )}

            {showSearch && (
                <Search
                    onClose={() => setShowSearch(false)}
                    onSelectUser={handleUserSelect}
                />
            )}
        </div>
    );
};

export default Chat;
