import React, { useState, useEffect } from 'react';
import axios from 'axios';
import toast from 'react-hot-toast';
import './CreateGroup.css';

const CreateGroup = ({ onClose, onGroupCreated }) => {
    const [currentStep, setCurrentStep] = useState(1); // Step tracker
    const [groupName, setGroupName] = useState('');
    const [groupDescription, setGroupDescription] = useState('');
    const [selectedMembers, setSelectedMembers] = useState([]);
    const [availableUsers, setAvailableUsers] = useState([]);
    const [loading, setLoading] = useState(false);
    const [creating, setCreating] = useState(false);
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedIcon, setSelectedIcon] = useState('ðŸ‘¥'); // Default group icon

    // Guruh iconlari
    const groupIcons = [
        'ðŸ‘¥', 'ðŸ’¬', 'ðŸŽ¯', 'ðŸš€', 'ðŸ’¡', 'ðŸŽ¨', 'ðŸ“š', 'ðŸŽµ',
        'âš½', 'ðŸŽ®', 'ðŸ’»', 'ðŸ“±', 'ðŸ•', 'â˜•', 'ðŸŒŸ', 'ðŸ”¥',
        'ðŸ’Ž', 'ðŸŽª', 'ðŸŽ­', 'ðŸŽ¨', 'ðŸŽ¯', 'ðŸŽ²', 'ðŸŽ¸', 'ðŸŽ¤'
    ];

    const totalSteps = 3;

    useEffect(() => {
        loadAvailableUsers();
    }, []);

    const loadAvailableUsers = async () => {
        try {
            setLoading(true);
            const token = localStorage.getItem('token');
            console.log('Loading users with token:', token ? 'exists' : 'missing');

            const response = await axios.get('http://localhost:5005/api/users/all', {
                headers: { Authorization: `Bearer ${token}` }
            });

            console.log('Users response:', response.data);

            if (response.data.success) {
                setAvailableUsers(response.data.users);
                console.log('Available users set:', response.data.users.length);
            } else {
                console.error('Failed to get users:', response.data);
                toast.error('Foydalanuvchilarni olishda xato');
            }
        } catch (error) {
            console.error('Failed to load users:', error);
            toast.error('Foydalanuvchilarni yuklashda xato: ' + (error.response?.data?.message || error.message));
        } finally {
            setLoading(false);
        }
    };

    const handleMemberToggle = (userId) => {
        console.log('Toggling member:', userId);
        setSelectedMembers(prev => {
            const isSelected = prev.includes(userId);
            console.log('Current selected:', prev);
            console.log('Is selected:', isSelected);

            if (isSelected) {
                const newSelected = prev.filter(id => id !== userId);
                console.log('Removing member, new list:', newSelected);
                return newSelected;
            } else {
                const newSelected = [...prev, userId];
                console.log('Adding member, new list:', newSelected);
                return newSelected;
            }
        });
    };

    const handleCreateGroup = async () => {
        if (!groupName.trim()) {
            toast.error('Guruh nomi kerak');
            return;
        }

        if (groupName.trim().length > 50) {
            toast.error('Guruh nomi 50 belgidan oshmasligi kerak');
            return;
        }

        try {
            setCreating(true);
            const token = localStorage.getItem('token');

            // Guruh ma'lumotlarini saqlash
            const groupData = {
                name: groupName.trim(),
                description: groupDescription.trim(),
                members: selectedMembers,
                isPublic: false,
                icon: selectedIcon
            };

            const response = await axios.post('http://localhost:5005/api/groups', groupData, {
                headers: { Authorization: `Bearer ${token}` }
            });

            if (response.data.success) {
                toast.success('Guruh muvaffaqiyatli yaratildi');

                // Guruh yaratilgandan keyin ma'lumotlarni tozalash
                setGroupName('');
                setGroupDescription('');
                setSelectedMembers([]);
                setSelectedIcon('ðŸ‘¥');
                setCurrentStep(1);

                onGroupCreated(response.data.group);
                onClose();
            }
        } catch (error) {
            const message = error.response?.data?.message || 'Guruh yaratishda xato';
            toast.error(message);

            // Xato bo'lsa, modal yopilmasin
            console.error('Group creation error:', error);
        } finally {
            setCreating(false);
        }
    };

    const filteredUsers = availableUsers.filter(user =>
        user.username.toLowerCase().includes(searchTerm.toLowerCase())
    );

    // Step navigation functions with validation
    const nextStep = () => {
        if (currentStep < totalSteps) {
            // Step 1 validation
            if (currentStep === 1) {
                if (!groupName.trim()) {
                    toast.error('Guruh nomi kiritilishi kerak');
                    return;
                }
                if (groupName.trim().length > 50) {
                    toast.error('Guruh nomi 50 belgidan oshmasligi kerak');
                    return;
                }
            }

            // Step 2 validation
            if (currentStep === 2) {
                if (selectedMembers.length === 0) {
                    toast.error('Kamida bitta a\'zo tanlash kerak');
                    return;
                }
            }

            setCurrentStep(currentStep + 1);
        }
    };

    const prevStep = () => {
        if (currentStep > 1) {
            setCurrentStep(currentStep - 1);
        }
    };

    const canProceedToStep2 = () => {
        return groupName.trim().length > 0;
    };

    const canProceedToStep3 = () => {
        return selectedMembers.length > 0;
    };

    // Step titles
    const getStepTitle = () => {
        switch (currentStep) {
            case 1: return 'Guruh Ma\'lumotlari';
            case 2: return 'A\'zolarni Tanlash';
            case 3: return 'Tasdiqlash';
            default: return 'Yangi Guruh';
        }
    };

    // Modal yopishdan oldin tasdiqlash
    const handleClose = () => {
        const hasUnsavedData = groupName.trim() || groupDescription.trim() || selectedMembers.length > 0;

        if (hasUnsavedData && !creating) {
            const confirmClose = window.confirm('Saqlanmagan ma\'lumotlar yo\'qoladi. Rostdan ham chiqmoqchimisiz?');
            if (!confirmClose) {
                return;
            }
        }

        // Ma'lumotlarni tozalash
        setGroupName('');
        setGroupDescription('');
        setSelectedMembers([]);
        setSelectedIcon('ðŸ‘¥');
        setCurrentStep(1);
        setSearchTerm('');

        onClose();
    };

    return (
        <div className="create-group-overlay">
            <div className="create-group-modal">
                <div className="create-group-header">
                    <div className="header-content">
                        <h2>{getStepTitle()}</h2>
                        <div className="step-indicator">
                            <span className="step-text">{currentStep} / {totalSteps}</span>
                        </div>
                    </div>
                    <button className="close-button" onClick={handleClose}>
                        <svg viewBox="0 0 24 24" fill="currentColor">
                            <path d="M19 6.41L17.59 5 12 10.59 6.41 5 5 6.41 10.59 12 5 17.59 6.41 19 12 13.41 17.59 19 19 17.59 13.41 12z" />
                        </svg>
                    </button>
                </div>

                {/* Progress Bar */}
                <div className="progress-bar">
                    <div
                        className="progress-fill"
                        style={{ width: `${(currentStep / totalSteps) * 100}%` }}
                    ></div>
                </div>

                <div className="create-group-content">
                    {/* Step 1: Group Info */}
                    {currentStep === 1 && (
                        <div className="step-content">
                            <div className="form-section">
                                <div className="form-group">
                                    <label>Guruh Iconi</label>
                                    <div className="icon-selector">
                                        <div className="selected-icon-display">
                                            <span className="selected-icon">{selectedIcon}</span>
                                        </div>
                                        <div className="icon-grid">
                                            {groupIcons.map(icon => (
                                                <button
                                                    key={icon}
                                                    type="button"
                                                    className={`icon-option ${selectedIcon === icon ? 'selected' : ''}`}
                                                    onClick={() => setSelectedIcon(icon)}
                                                >
                                                    {icon}
                                                </button>
                                            ))}
                                        </div>
                                    </div>
                                </div>

                                <div className="form-group">
                                    <label htmlFor="groupName">Guruh Nomi *</label>
                                    <input
                                        type="text"
                                        id="groupName"
                                        value={groupName}
                                        onChange={(e) => setGroupName(e.target.value)}
                                        placeholder="Guruh nomini kiriting..."
                                        maxLength={50}
                                        required
                                    />
                                    <span className="char-count">{groupName.length}/50</span>
                                </div>

                                <div className="form-group">
                                    <label htmlFor="groupDescription">Tavsif (ixtiyoriy)</label>
                                    <textarea
                                        id="groupDescription"
                                        value={groupDescription}
                                        onChange={(e) => setGroupDescription(e.target.value)}
                                        placeholder="Guruh haqida qisqacha ma'lumot..."
                                        maxLength={200}
                                        rows={3}
                                    />
                                    <span className="char-count">{groupDescription.length}/200</span>
                                </div>
                            </div>
                        </div>
                    )}

                    {/* Step 2: Select Members */}
                    {currentStep === 2 && (
                        <div className="step-content">
                            <div className="form-section">
                                <div className="search-members">
                                    <input
                                        type="text"
                                        placeholder="Foydalanuvchilarni qidirish..."
                                        value={searchTerm}
                                        onChange={(e) => setSearchTerm(e.target.value)}
                                        className="member-search-input"
                                    />
                                </div>

                                {selectedMembers.length > 0 && (
                                    <div className="selected-members">
                                        <h4>Tanlangan a'zolar ({selectedMembers.length})</h4>
                                        <div className="selected-members-list">
                                            {selectedMembers.map(memberId => {
                                                const user = availableUsers.find(u => u.id === memberId);
                                                return user ? (
                                                    <div key={memberId} className="selected-member">
                                                        <img src={user.avatar} alt={user.username} />
                                                        <span>{user.username}</span>
                                                        <button
                                                            type="button"
                                                            onClick={() => handleMemberToggle(memberId)}
                                                            className="remove-member"
                                                        >
                                                            <svg viewBox="0 0 24 24" fill="currentColor">
                                                                <path d="M19 6.41L17.59 5 12 10.59 6.41 5 5 6.41 10.59 12 5 17.59 6.41 19 12 13.41 17.59 19 19 17.59 13.41 12z" />
                                                            </svg>
                                                        </button>
                                                    </div>
                                                ) : null;
                                            })}
                                        </div>
                                    </div>
                                )}

                                <div className="available-members">
                                    {loading ? (
                                        <div className="loading-members">
                                            <div className="spinner"></div>
                                            <p>Foydalanuvchilar yuklanmoqda...</p>
                                        </div>
                                    ) : (
                                        <div className="members-list">
                                            {filteredUsers.length === 0 ? (
                                                <div className="no-users">
                                                    <p>Foydalanuvchilar topilmadi</p>
                                                </div>
                                            ) : (
                                                filteredUsers.map(user => (
                                                    <div
                                                        key={user.id}
                                                        className={`member-item ${selectedMembers.includes(user.id) ? 'selected' : ''}`}
                                                        onClick={() => handleMemberToggle(user.id)}
                                                    >
                                                        <div className="member-avatar-container">
                                                            <img src={user.avatar} alt={user.username} />
                                                        </div>
                                                        <div className="member-info">
                                                            <div className="member-name">{user.username}</div>
                                                            <div className={`member-status ${user.isOnline ? 'online' : 'offline'}`}>
                                                                {user.isOnline ? 'Online' : 'Offline'}
                                                            </div>
                                                        </div>
                                                        <div className="member-checkbox">
                                                            <input
                                                                type="checkbox"
                                                                checked={selectedMembers.includes(user.id)}
                                                                onChange={(e) => {
                                                                    e.stopPropagation();
                                                                    handleMemberToggle(user.id);
                                                                }}
                                                                onClick={(e) => e.stopPropagation()}
                                                            />
                                                        </div>
                                                    </div>
                                                ))
                                            )}
                                        </div>
                                    )}
                                </div>
                            </div>
                        </div>
                    )}

                    {/* Step 3: Confirmation */}
                    {currentStep === 3 && (
                        <div className="step-content">
                            <div className="confirmation-section">
                                <div className="group-preview">
                                    <div className="preview-header">
                                        <div className="preview-icon">{selectedIcon}</div>
                                        <div className="preview-info">
                                            <h3>{groupName}</h3>
                                            {groupDescription && <p>{groupDescription}</p>}
                                        </div>
                                    </div>

                                    <div className="preview-stats">
                                        <div className="stat-item">
                                            <span className="stat-number">{selectedMembers.length}</span>
                                            <span className="stat-label">A'zo</span>
                                        </div>
                                    </div>

                                    <div className="preview-members">
                                        <h4>Guruh a'zolari:</h4>
                                        <div className="members-preview-list">
                                            {selectedMembers.map(memberId => {
                                                const user = availableUsers.find(u => u.id === memberId);
                                                return user ? (
                                                    <div key={memberId} className="member-preview">
                                                        <img src={user.avatar} alt={user.username} />
                                                        <span>{user.username}</span>
                                                    </div>
                                                ) : null;
                                            })}
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    )}
                </div>

                {/* Navigation Footer */}
                <div className="form-footer">
                    <div className="footer-buttons">
                        {currentStep > 1 && (
                            <button
                                type="button"
                                className="nav-button prev-button"
                                onClick={prevStep}
                            >
                                <svg viewBox="0 0 24 24" fill="currentColor">
                                    <path d="M20 11H7.83l5.59-5.59L12 4l-8 8 8 8 1.41-1.41L7.83 13H20v-2z" />
                                </svg>
                                Orqaga
                            </button>
                        )}

                        <button type="button" className="cancel-button" onClick={handleClose}>
                            Bekor qilish
                        </button>

                        {currentStep < totalSteps ? (
                            <button
                                type="button"
                                className="nav-button next-button"
                                onClick={nextStep}
                                disabled={
                                    (currentStep === 1 && !canProceedToStep2()) ||
                                    (currentStep === 2 && !canProceedToStep3())
                                }
                            >
                                Keyingi
                                <svg viewBox="0 0 24 24" fill="currentColor">
                                    <path d="M4 11v2h12.17l-5.59 5.59L12 20l8-8-8-8-1.41 1.41L16.17 11H4z" />
                                </svg>
                            </button>
                        ) : (
                            <button
                                type="button"
                                className="create-button"
                                onClick={handleCreateGroup}
                                disabled={creating}
                            >
                                {creating ? (
                                    <>
                                        <div className="spinner-small"></div>
                                        Yaratilmoqda...
                                    </>
                                ) : (
                                    'Guruh Yaratish'
                                )}
                            </button>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default CreateGroup;
