import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import toast from 'react-hot-toast';
import './Auth.css';

const Login = () => {
    const [phone, setPhone] = useState('');
    const [phoneError, setPhoneError] = useState('');
    const { login, loading, error, isAuthenticated, clearError } = useAuth();
    const navigate = useNavigate();

    useEffect(() => {
        if (isAuthenticated) {
            navigate('/chat');
        }
    }, [isAuthenticated, navigate]);

    useEffect(() => {
        if (error) {
            toast.error(error);
            clearError();
        }
    }, [error, clearError]);

    const onSubmit = async (e) => {
        e.preventDefault();

        if (!phone.trim()) {
            setPhoneError('Phone number is required');
            return;
        }

        if (phone.length < 13) {
            setPhoneError('Please enter a complete phone number');
            return;
        }

        setPhoneError('');
        const result = await login({ phone: phone });
        if (result.success) {
            toast.success('Login successful!');
            navigate('/chat');
        }
    };

    const formatPhone = (value) => {
        const digits = value.replace(/\D/g, '');
        let formatted = '+998';

        if (digits.length > 3) {
            const remaining = digits.slice(3);

            if (remaining.length > 0) {
                formatted += ' ' + remaining.slice(0, 2);
            }
            if (remaining.length > 2) {
                formatted += ' ' + remaining.slice(2, 5);
            }
            if (remaining.length > 5) {
                formatted += ' ' + remaining.slice(5, 7);
            }
            if (remaining.length > 7) {
                formatted += ' ' + remaining.slice(7, 9);
            }
        }

        return formatted;
    };

    const handlePhoneChange = (e) => {
        let value = e.target.value;

        if (!value.startsWith('+998')) {
            value = '+998' + value.replace(/^\+?998?/, '');
        }

        const formatted = formatPhone(value);
        setPhone(formatted);
        setPhoneError('');
    };

    return (
        <div className="auth-container">
            <div className="auth-background">
                <div className="auth-card">
                    <div className="auth-header">
                        <div className="auth-logo">
                            <div className="logo-icon">
                                <svg viewBox="0 0 24 24" fill="currentColor">
                                    <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm-2 15l-5-5 1.41-1.41L10 14.17l7.59-7.59L19 8l-9 9z" />
                                </svg>
                            </div>
                            <h1>Telegram Clone</h1>
                        </div>
                        <p className="auth-subtitle">Enter your phone number to continue</p>
                    </div>

                    <form className="auth-form" onSubmit={onSubmit}>
                        <div className="form-group">
                            <label htmlFor="phone">Phone Number</label>
                            <div className="input-wrapper">
                                <input
                                    type="text"
                                    id="phone"
                                    className={`form-input ${phoneError ? 'error' : ''}`}
                                    placeholder="+998 XX XXX XX XX"
                                    value={phone}
                                    onChange={handlePhoneChange}
                                    maxLength={17}
                                />
                                <div className="input-icon">
                                    <svg viewBox="0 0 24 24" fill="currentColor">
                                        <path d="M6.62 10.79c1.44 2.83 3.76 5.14 6.59 6.59l2.2-2.2c.27-.27.67-.36 1.02-.24 1.12.37 2.33.57 3.57.57.55 0 1 .45 1 1V20c0 .55-.45 1-1 1-9.39 0-17-7.61-17-17 0-.55.45-1 1-1h3.5c.55 0 1 .45 1 1 0 1.25.2 2.45.57 3.57.11.35.03.74-.25 1.02l-2.2 2.2z" />
                                    </svg>
                                </div>
                            </div>
                            {phoneError && <span className="error-message">{phoneError}</span>}
                        </div>

                        <div className="phone-info">
                            <p>We'll connect you instantly - no SMS needed!</p>
                            <p><strong>Demo:</strong> +998 90 123 45 67, +998 91 234 56 78</p>
                        </div>

                        <button
                            type="submit"
                            className={`auth-button ${loading ? 'loading' : ''}`}
                            disabled={loading}
                        >
                            {loading ? (
                                <>
                                    <div className="spinner"></div>
                                    Connecting...
                                </>
                            ) : (
                                'Continue'
                            )}
                        </button>
                    </form>

                    <div className="auth-footer">
                        <p>
                            Don't have Telegram?{' '}
                            <Link to="/register" className="auth-link">
                                Sign up here
                            </Link>
                        </p>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Login;
