import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import './App.css';

const socket = io('http://localhost:5000');

function App() {
    const [user, setUser] = useState(null);
    const [username, setUsername] = useState('');
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');
    const [onlineUsers, setOnlineUsers] = useState([]);
    const [typingUsers, setTypingUsers] = useState([]);
    const [isTyping, setIsTyping] = useState(false);

    const messagesEndRef = useRef(null);
    const typingTimeoutRef = useRef(null);

    useEffect(() => {
        // Socket event listeners
        socket.on('user-joined', (userData) => {
            setUser(userData);
        });

        socket.on('message-history', (history) => {
            setMessages(history);
        });

        socket.on('new-message', (message) => {
            setMessages(prev => [...prev, message]);
        });

        socket.on('online-users', (users) => {
            setOnlineUsers(users);
        });

        socket.on('user-typing', (data) => {
            if (data.isTyping) {
                setTypingUsers(prev => {
                    if (!prev.find(u => u.id === data.user.id)) {
                        return [...prev, data.user];
                    }
                    return prev;
                });
            } else {
                setTypingUsers(prev => prev.filter(u => u.id !== data.user.id));
            }
        });

        return () => {
            socket.off('user-joined');
            socket.off('message-history');
            socket.off('new-message');
            socket.off('online-users');
            socket.off('user-typing');
        };
    }, []);

    useEffect(() => {
        scrollToBottom();
    }, [messages]);

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    };

    const handleLogin = (e) => {
        e.preventDefault();
        if (username.trim()) {
            socket.emit('join', { username: username.trim() });
        }
    };

    const handleSendMessage = (e) => {
        e.preventDefault();
        if (newMessage.trim()) {
            socket.emit('send-message', { text: newMessage.trim() });
            setNewMessage('');
            handleStopTyping();
        }
    };

    const handleTyping = (e) => {
        setNewMessage(e.target.value);

        if (!isTyping) {
            setIsTyping(true);
            socket.emit('typing', { isTyping: true });
        }

        clearTimeout(typingTimeoutRef.current);
        typingTimeoutRef.current = setTimeout(() => {
            handleStopTyping();
        }, 1000);
    };

    const handleStopTyping = () => {
        if (isTyping) {
            setIsTyping(false);
            socket.emit('typing', { isTyping: false });
        }
        clearTimeout(typingTimeoutRef.current);
    };

    const formatTime = (timestamp) => {
        return new Date(timestamp).toLocaleTimeString('en-US', {
            hour: '2-digit',
            minute: '2-digit'
        });
    };

    if (!user) {
        return (
            <div className="login-container">
                <form className="login-form" onSubmit={handleLogin}>
                    <h1>Telegram Clone</h1>
                    <input
                        type="text"
                        placeholder="Enter your username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        required
                    />
                    <button type="submit">Join Chat</button>
                </form>
            </div>
        );
    }

    return (
        <div className="app">
            <div className="chat-container">
                <div className="sidebar">
                    <div className="sidebar-header">
                        <img src={user.avatar} alt={user.username} className="user-avatar" />
                        <div className="user-info">
                            <h3>{user.username}</h3>
                            <div className="user-status">Online</div>
                        </div>
                    </div>

                    <div className="online-users">
                        <h4>Online Users ({onlineUsers.length})</h4>
                        {onlineUsers.map(onlineUser => (
                            <div key={onlineUser.id} className="user-item">
                                <img src={onlineUser.avatar} alt={onlineUser.username} />
                                <span>{onlineUser.username}</span>
                            </div>
                        ))}
                    </div>
                </div>

                <div className="main-chat">
                    <div className="chat-header">
                        <h2>General Chat</h2>
                    </div>

                    <div className="messages-container">
                        {messages.map(message => (
                            <div
                                key={message.id}
                                className={`message ${message.user.id === user.id ? 'own' : ''}`}
                            >
                                <img
                                    src={message.user.avatar}
                                    alt={message.user.username}
                                    className="message-avatar"
                                />
                                <div className="message-content">
                                    <div className="message-header">
                                        <span className="message-username">{message.user.username}</span>
                                        <span className="message-time">{formatTime(message.timestamp)}</span>
                                    </div>
                                    <div className="message-text">{message.text}</div>
                                </div>
                            </div>
                        ))}

                        {typingUsers.length > 0 && (
                            <div className="typing-indicator">
                                <span>
                                    {typingUsers.map(u => u.username).join(', ')}
                                    {typingUsers.length === 1 ? ' is' : ' are'} typing
                                </span>
                                <div className="typing-dots">
                                    <div className="typing-dot"></div>
                                    <div className="typing-dot"></div>
                                    <div className="typing-dot"></div>
                                </div>
                            </div>
                        )}

                        <div ref={messagesEndRef} />
                    </div>

                    <div className="message-input-container">
                        <form className="message-input-form" onSubmit={handleSendMessage}>
                            <input
                                type="text"
                                className="message-input"
                                placeholder="Type a message..."
                                value={newMessage}
                                onChange={handleTyping}
                                onBlur={handleStopTyping}
                            />
                            <button
                                type="submit"
                                className="send-button"
                                disabled={!newMessage.trim()}
                            >
                                Send
                            </button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default App;
