import React, { useState, useEffect, useRef } from 'react';
import { useAuth } from '../../context/AuthContext';
import { authAPI } from '../../services/api';
import toast from 'react-hot-toast';
import './Profile.css';

const Profile = ({ onClose }) => {
    const { user, logout } = useAuth();
    const [isEditing, setIsEditing] = useState(false);
    const [loading, setLoading] = useState(false);
    const [avatarFile, setAvatarFile] = useState(null);
    const [avatarPreview, setAvatarPreview] = useState(user?.avatar || '');
    const fileInputRef = useRef(null);
    const [formData, setFormData] = useState({
        username: user?.username || '',
        firstName: user?.firstName || '',
        lastName: user?.lastName || '',
        bio: user?.bio || ''
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleFileSelect = (e) => {
        const file = e.target.files[0];
        if (file) {
            // Fayl o'lchamini tekshirish (5MB gacha)
            if (file.size > 5 * 1024 * 1024) {
                toast.error('Fayl o\'lchami 5MB dan kichik bo\'lishi kerak');
                return;
            }

            // Fayl turini tekshirish
            if (!file.type.startsWith('image/')) {
                toast.error('Faqat rasm fayllari qabul qilinadi');
                return;
            }

            setAvatarFile(file);

            // Preview uchun
            const reader = new FileReader();
            reader.onload = (e) => {
                setAvatarPreview(e.target.result);
            };
            reader.readAsDataURL(file);
        }
    };

    const handleAvatarUpload = () => {
        fileInputRef.current?.click();
    };

    const handleRemoveAvatar = () => {
        setAvatarFile(null);
        setAvatarPreview(user?.avatar || '');
        if (fileInputRef.current) {
            fileInputRef.current.value = '';
        }
    };

    const handleSave = async () => {
        setLoading(true);
        try {
            const response = await authAPI.updateProfile(formData);
            if (response.data.success) {
                toast.success('Profile updated successfully!');
                setIsEditing(false);
                // Update user context if needed
            }
        } catch (error) {
            toast.error(error.response?.data?.message || 'Failed to update profile');
        } finally {
            setLoading(false);
        }
    };

    const handleCancel = () => {
        setFormData({
            username: user?.username || '',
            firstName: user?.firstName || '',
            lastName: user?.lastName || '',
            bio: user?.bio || ''
        });
        setIsEditing(false);
    };

    const handleAvatarChange = async () => {
        // Generate new random avatar
        const newAvatar = `https://ui-avatars.com/api/?name=${formData.username}&background=random&color=fff&size=200&${Date.now()}`;

        try {
            const response = await authAPI.updatePhoto({ avatar: newAvatar });
            if (response.data.success) {
                toast.success('Profile photo updated!');
                // Force re-render by updating user context
                window.location.reload();
            }
        } catch (error) {
            toast.error('Failed to update photo');
        }
    };

    const handleLogout = async () => {
        await logout();
        toast.success('Logged out successfully');
        onClose();
    };

    return (
        <div className="profile-overlay">
            <div className="profile-modal">
                <div className="profile-header">
                    <h2>Profile</h2>
                    <button className="close-button" onClick={onClose}>
                        <svg viewBox="0 0 24 24" fill="currentColor">
                            <path d="M19 6.41L17.59 5 12 10.59 6.41 5 5 6.41 10.59 12 5 17.59 6.41 19 12 13.41 17.59 19 19 17.59 13.41 12z" />
                        </svg>
                    </button>
                </div>

                <div className="profile-content">
                    <div className="profile-avatar-section">
                        <div className="profile-avatar">
                            <img src={user?.avatar} alt={user?.username} />
                            <button className="avatar-change-btn" onClick={handleAvatarChange}>
                                <svg viewBox="0 0 24 24" fill="currentColor">
                                    <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm-2 15l-5-5 1.41-1.41L10 14.17l7.59-7.59L19 8l-9 9z" />
                                </svg>
                            </button>
                        </div>
                        <p className="profile-phone">{user?.phone}</p>
                    </div>

                    <div className="profile-form">
                        <div className="form-group">
                            <label>Username</label>
                            <input
                                type="text"
                                name="username"
                                value={formData.username}
                                onChange={handleInputChange}
                                disabled={!isEditing}
                                className={`profile-input ${!isEditing ? 'disabled' : ''}`}
                            />
                        </div>

                        <div className="form-row">
                            <div className="form-group">
                                <label>First Name</label>
                                <input
                                    type="text"
                                    name="firstName"
                                    value={formData.firstName}
                                    onChange={handleInputChange}
                                    disabled={!isEditing}
                                    className={`profile-input ${!isEditing ? 'disabled' : ''}`}
                                    placeholder="First name"
                                />
                            </div>

                            <div className="form-group">
                                <label>Last Name</label>
                                <input
                                    type="text"
                                    name="lastName"
                                    value={formData.lastName}
                                    onChange={handleInputChange}
                                    disabled={!isEditing}
                                    className={`profile-input ${!isEditing ? 'disabled' : ''}`}
                                    placeholder="Last name"
                                />
                            </div>
                        </div>

                        <div className="form-group">
                            <label>Bio</label>
                            <textarea
                                name="bio"
                                value={formData.bio}
                                onChange={handleInputChange}
                                disabled={!isEditing}
                                className={`profile-textarea ${!isEditing ? 'disabled' : ''}`}
                                placeholder="Tell us about yourself..."
                                rows="3"
                            />
                        </div>
                    </div>

                    <div className="profile-actions">
                        {!isEditing ? (
                            <>
                                <button className="edit-button" onClick={() => setIsEditing(true)}>
                                    <svg viewBox="0 0 24 24" fill="currentColor">
                                        <path d="M3 17.25V21h3.75L17.81 9.94l-3.75-3.75L3 17.25zM20.71 7.04c.39-.39.39-1.02 0-1.41l-2.34-2.34c-.39-.39-1.02-.39-1.41 0l-1.83 1.83 3.75 3.75 1.83-1.83z" />
                                    </svg>
                                    Edit Profile
                                </button>
                                <button className="logout-button" onClick={handleLogout}>
                                    <svg viewBox="0 0 24 24" fill="currentColor">
                                        <path d="M17 7l-1.41 1.41L18.17 11H8v2h10.17l-2.58 2.59L17 17l5-5zM4 5h8V3H4c-1.1 0-2 .9-2 2v14c0 1.1.9 2 2 2h8v-2H4V5z" />
                                    </svg>
                                    Logout
                                </button>
                            </>
                        ) : (
                            <>
                                <button
                                    className="save-button"
                                    onClick={handleSave}
                                    disabled={loading}
                                >
                                    {loading ? 'Saving...' : 'Save Changes'}
                                </button>
                                <button className="cancel-button" onClick={handleCancel}>
                                    Cancel
                                </button>
                            </>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Profile;
