import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import { useAuth } from '../../context/AuthContext';
import { messagesAPI } from '../../services/api';
import toast from 'react-hot-toast';
import Profile from '../Profile/Profile';
import Search from '../Search/Search';
import './Chat.css';

const Chat = () => {
    const { user, token, logout } = useAuth();
    const [socket, setSocket] = useState(null);
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');
    const [onlineUsers, setOnlineUsers] = useState([]);
    const [typingUsers, setTypingUsers] = useState(new Map());
    const [isTyping, setIsTyping] = useState(false);
    const [connected, setConnected] = useState(false);
    const [connectionStatus, setConnectionStatus] = useState('Connecting...');
    const [showProfile, setShowProfile] = useState(false);
    const [showSearch, setShowSearch] = useState(false);
    const [lastActivity, setLastActivity] = useState(new Date());
    const [currentChatId, setCurrentChatId] = useState('general');
    const [loading, setLoading] = useState(false);
    const [hasMore, setHasMore] = useState(false); // Disabled for in-memory server
    const [page, setPage] = useState(1);

    const messagesEndRef = useRef(null);
    const typingTimeoutRef = useRef(null);
    const messagesContainerRef = useRef(null);

    // Load messages from API (disabled for in-memory server)
    const loadMessages = async (chatId = currentChatId, pageNum = 1, append = false) => {
        // For in-memory server, we don't have pagination API
        // Messages are loaded via socket connection
        console.log('Load messages called, but using socket messages instead');
        return;
    };

    // Load more messages (disabled for in-memory server)
    const loadMoreMessages = () => {
        // No pagination for in-memory server
        toast.info('All messages are already loaded');
        setHasMore(false);
    };

    // Send message via API
    const sendMessageAPI = async (messageData) => {
        try {
            const response = await messagesAPI.sendMessage({
                ...messageData,
                chatId: currentChatId
            });

            if (response.data.success) {
                // Message will be received via socket
                return response.data.data;
            }
        } catch (error) {
            console.error('Send message API error:', error);
            toast.error('Failed to send message');
            throw error;
        }
    };

    useEffect(() => {
        if (user && token) {
            const newSocket = io('http://localhost:5005', {
                auth: {
                    token: token
                },
                transports: ['websocket', 'polling']
            });

            newSocket.on('connect', () => {
                console.log('âœ… Connected to server');
                setConnected(true);
                setConnectionStatus('Connected');
                toast.success('Connected to chat!', {
                    icon: 'ðŸŸ¢',
                    duration: 2000
                });
            });

            newSocket.on('disconnect', (reason) => {
                console.log('âŒ Disconnected from server:', reason);
                setConnected(false);
                setConnectionStatus('Disconnected');
                setTypingUsers(new Map());
                toast.error('Disconnected from chat', {
                    icon: 'ðŸ”´',
                    duration: 3000
                });
            });

            newSocket.on('connect_error', (error) => {
                console.error('Connection error:', error);
                setConnectionStatus('Connection failed');
                toast.error('Failed to connect to chat');
            });

            newSocket.on('reconnect', (attemptNumber) => {
                console.log('ðŸ”„ Reconnected after', attemptNumber, 'attempts');
                setConnected(true);
                setConnectionStatus('Reconnected');
                toast.success('Reconnected to chat!', {
                    icon: 'ðŸ”„',
                    duration: 2000
                });
            });

            newSocket.on('reconnecting', (attemptNumber) => {
                console.log('ðŸ”„ Reconnecting... attempt', attemptNumber);
                setConnectionStatus(`Reconnecting... (${attemptNumber})`);
            });

            newSocket.on('user-joined', (userData) => {
                console.log('ðŸ‘‹ User joined:', userData);
                setLastActivity(new Date());
                // Don't auto-load messages, let socket handle it
            });

            newSocket.on('message-history', (history) => {
                setMessages(history);
                setLastActivity(new Date());
            });

            newSocket.on('new-message', (message) => {
                setMessages(prev => [...prev, message]);
                setLastActivity(new Date());

                // Play notification sound for other users' messages
                if (message.user.id !== user.id) {
                    // You can add sound notification here
                    console.log('ðŸ“¨ New message from:', message.user.username);
                }
            });

            newSocket.on('online-users', (users) => {
                setOnlineUsers(users);
                setLastActivity(new Date());
            });

            newSocket.on('user-online', (userData) => {
                toast.success(`${userData.username} joined the chat`, {
                    icon: 'ðŸ‘‹',
                    duration: 3000
                });
                setLastActivity(new Date());
            });

            newSocket.on('user-offline', (userData) => {
                toast(`${userData.username} left the chat`, {
                    icon: 'ðŸ‘‹',
                    duration: 3000
                });

                // Remove from typing users if they were typing
                setTypingUsers(prev => {
                    const newMap = new Map(prev);
                    newMap.delete(userData.id);
                    return newMap;
                });
                setLastActivity(new Date());
            });

            newSocket.on('user-typing', (data) => {
                setTypingUsers(prev => {
                    const newMap = new Map(prev);
                    if (data.isTyping) {
                        newMap.set(data.user.id, {
                            ...data.user,
                            timestamp: new Date()
                        });
                    } else {
                        newMap.delete(data.user.id);
                    }
                    return newMap;
                });
                setLastActivity(new Date());
            });

            newSocket.on('message-error', (error) => {
                toast.error(error.message, {
                    icon: 'âŒ',
                    duration: 4000
                });
            });

            setSocket(newSocket);

            return () => {
                newSocket.close();
            };
        }
    }, [user, token]);

    // Send user activity periodically
    useEffect(() => {
        if (socket && connected) {
            const activityInterval = setInterval(() => {
                socket.emit('user-activity');
            }, 30000); // Every 30 seconds

            return () => clearInterval(activityInterval);
        }
    }, [socket, connected]);

    useEffect(() => {
        scrollToBottom();
    }, [messages]);

    // Auto-clear old typing indicators
    useEffect(() => {
        const cleanupInterval = setInterval(() => {
            const now = new Date();
            setTypingUsers(prev => {
                const newMap = new Map();
                prev.forEach((user, id) => {
                    if (now - user.timestamp < 5000) { // Keep for 5 seconds
                        newMap.set(id, user);
                    }
                });
                return newMap;
            });
        }, 1000);

        return () => clearInterval(cleanupInterval);
    }, []);

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    };

    const handleSendMessage = async (e) => {
        e.preventDefault();
        if (newMessage.trim() && socket && connected) {
            const messageText = newMessage.trim();
            setNewMessage('');
            handleStopTyping();

            // Send via socket only (simpler approach)
            socket.emit('send-message', {
                text: messageText,
                chatId: currentChatId
            });
        }
    };

    const handleTyping = (e) => {
        setNewMessage(e.target.value);

        if (!isTyping && socket && connected && e.target.value.trim()) {
            setIsTyping(true);
            socket.emit('typing', {
                isTyping: true,
                chatId: currentChatId
            });
        }

        clearTimeout(typingTimeoutRef.current);
        typingTimeoutRef.current = setTimeout(() => {
            handleStopTyping();
        }, 1500); // Stop typing after 1.5 seconds of inactivity
    };

    const handleStopTyping = () => {
        if (isTyping && socket && connected) {
            setIsTyping(false);
            socket.emit('typing', {
                isTyping: false,
                chatId: currentChatId
            });
        }
        clearTimeout(typingTimeoutRef.current);
    };

    const handleLogout = async () => {
        if (socket) {
            socket.close();
        }
        await logout();
        toast.success('Logged out successfully');
    };

    const handleUserSelect = (selectedUser) => {
        toast.success(`Starting chat with ${selectedUser.username}`);
        // Here you can implement private chat functionality
        // For now, we'll just show a message
    };

    const formatTime = (timestamp) => {
        return new Date(timestamp).toLocaleTimeString('en-US', {
            hour: '2-digit',
            minute: '2-digit'
        });
    };

    const formatLastSeen = (lastSeen) => {
        const now = new Date();
        const diff = now - new Date(lastSeen);
        const minutes = Math.floor(diff / 60000);
        const hours = Math.floor(diff / 3600000);
        const days = Math.floor(diff / 86400000);

        if (minutes < 1) return 'just now';
        if (minutes < 60) return `${minutes}m ago`;
        if (hours < 24) return `${hours}h ago`;
        return `${days}d ago`;
    };

    const getConnectionStatusColor = () => {
        if (connected) return '#4CAF50';
        if (connectionStatus.includes('Reconnecting')) return '#FF9800';
        return '#F44336';
    };

    if (!user) {
        return (
            <div className="loading-container">
                <div className="spinner"></div>
                <p>Loading user data...</p>
            </div>
        );
    }

    return (
        <div className="chat-container">
            <div className="sidebar">
                <div className="sidebar-header">
                    <div className="user-avatar-section" onClick={() => setShowProfile(true)}>
                        <img src={user.avatar} alt={user.username} className="user-avatar" />
                        <div className="user-info">
                            <h3>{user.username}</h3>
                            <div className={`user-status ${connected ? 'online' : 'offline'}`}>
                                {connected ? 'Online' : 'Offline'}
                            </div>
                        </div>
                    </div>
                    <button className="logout-button" onClick={handleLogout} title="Logout">
                        <svg viewBox="0 0 24 24" fill="currentColor">
                            <path d="M17 7l-1.41 1.41L18.17 11H8v2h10.17l-2.58 2.59L17 17l5-5zM4 5h8V3H4c-1.1 0-2 .9-2 2v14c0 1.1.9 2 2 2h8v-2H4V5z" />
                        </svg>
                    </button>
                </div>

                <div className="online-users">
                    <h4>Online Users ({onlineUsers.length})</h4>
                    {onlineUsers.map(onlineUser => (
                        <div key={onlineUser.id || onlineUser._id} className="user-item">
                            <div className="user-avatar-container">
                                <img src={onlineUser.avatar} alt={onlineUser.username} />
                                <div className={`user-status-dot ${onlineUser.isOnline ? 'online' : 'offline'}`}></div>
                            </div>
                            <div className="user-details">
                                <span className="user-name">{onlineUser.username}</span>
                                {onlineUser.id === user.id && <span className="you-badge">You</span>}
                                {onlineUser.isOnline ? (
                                    <span className="user-status online">Online</span>
                                ) : (
                                    <span className="user-status offline">
                                        Last seen {formatLastSeen(onlineUser.lastSeen)}
                                    </span>
                                )}
                            </div>
                        </div>
                    ))}
                    {onlineUsers.length === 0 && (
                        <div className="no-users">
                            <span>No users online</span>
                        </div>
                    )}
                </div>
            </div>

            <div className="main-chat">
                <div className="chat-header">
                    <h2>General Chat</h2>
                    <div className="header-actions">
                        <button
                            className="search-button"
                            onClick={() => setShowSearch(true)}
                            title="Search Users"
                        >
                            <svg viewBox="0 0 24 24" fill="currentColor">
                                <path d="M15.5 14h-.79l-.28-.27C15.41 12.59 16 11.11 16 9.5 16 5.91 13.09 3 9.5 3S3 5.91 3 9.5 5.91 16 9.5 16c1.61 0 3.09-.59 4.23-1.57l.27.28v.79l5 4.99L20.49 19l-4.99-5zm-6 0C7.01 14 5 11.99 5 9.5S7.01 5 9.5 5 14 7.01 14 9.5 11.99 14 9.5 14z" />
                            </svg>
                        </button>
                        <div className={`connection-status ${connected ? 'connected' : 'disconnected'}`}>
                            <div
                                className="status-dot"
                                style={{ backgroundColor: getConnectionStatusColor() }}
                            ></div>
                            <span className="status-text">{connectionStatus}</span>
                        </div>
                    </div>
                </div>

                <div className="messages-container" ref={messagesContainerRef}>
                    {loading && page === 1 && (
                        <div className="loading-messages">
                            <div className="spinner"></div>
                            <p>Loading messages...</p>
                        </div>
                    )}

                    {/* Load More button disabled for in-memory server */}
                    {false && hasMore && messages.length > 0 && (
                        <button
                            className="load-more-button"
                            onClick={loadMoreMessages}
                            disabled={loading}
                        >
                            {loading ? 'Loading...' : 'Load More Messages'}
                        </button>
                    )}

                    {messages.length === 0 && !loading ? (
                        <div className="empty-chat">
                            <div className="empty-icon">ðŸ’¬</div>
                            <h3>Welcome to the chat!</h3>
                            <p>Start a conversation by sending a message below.</p>
                        </div>
                    ) : (
                        messages.map(message => (
                            <div
                                key={message.id || message._id}
                                className={`message ${message.user.id === user.id ? 'own' : ''}`}
                            >
                                <img
                                    src={message.user.avatar}
                                    alt={message.user.username}
                                    className="message-avatar"
                                />
                                <div className="message-content">
                                    <div className="message-header">
                                        <span className="message-username">{message.user.username}</span>
                                        <span className="message-time">{formatTime(message.timestamp)}</span>
                                        {message.edited && <span className="edited-badge">edited</span>}
                                    </div>
                                    {message.replyTo && (
                                        <div className="reply-message">
                                            <span className="reply-username">{message.replyTo.sender.username}</span>
                                            <span className="reply-text">{message.replyTo.text}</span>
                                        </div>
                                    )}
                                    <div className="message-text">{message.text}</div>
                                    {message.fileUrl && (
                                        <div className="message-file">
                                            {message.messageType === 'image' ? (
                                                <img src={message.fileUrl} alt={message.fileName} className="message-image" />
                                            ) : (
                                                <a href={message.fileUrl} download={message.fileName} className="file-link">
                                                    ðŸ“Ž {message.fileName}
                                                </a>
                                            )}
                                        </div>
                                    )}
                                </div>
                            </div>
                        ))
                    )}

                    {Array.from(typingUsers.values()).length > 0 && (
                        <div className="typing-indicator">
                            <div className="typing-avatars">
                                {Array.from(typingUsers.values()).slice(0, 3).map(user => (
                                    <img
                                        key={user.id}
                                        src={user.avatar}
                                        alt={user.username}
                                        className="typing-avatar"
                                        title={`${user.username} is typing...`}
                                    />
                                ))}
                            </div>
                            <div className="typing-text">
                                <span>
                                    {Array.from(typingUsers.values()).length === 1
                                        ? `${Array.from(typingUsers.values())[0].username} is typing`
                                        : Array.from(typingUsers.values()).length === 2
                                            ? `${Array.from(typingUsers.values())[0].username} and ${Array.from(typingUsers.values())[1].username} are typing`
                                            : `${Array.from(typingUsers.values()).length} people are typing`
                                    }
                                </span>
                                <div className="typing-dots">
                                    <div className="typing-dot"></div>
                                    <div className="typing-dot"></div>
                                    <div className="typing-dot"></div>
                                </div>
                            </div>
                        </div>
                    )}

                    <div ref={messagesEndRef} />
                </div>

                <div className="message-input-container">
                    <form className="message-input-form" onSubmit={handleSendMessage}>
                        <input
                            type="text"
                            className="message-input"
                            placeholder={connected ? "Type a message..." : "Connecting..."}
                            value={newMessage}
                            onChange={handleTyping}
                            onBlur={handleStopTyping}
                            disabled={!connected}
                        />
                        <button
                            type="submit"
                            className="send-button"
                            disabled={!newMessage.trim() || !connected}
                        >
                            <svg viewBox="0 0 24 24" fill="currentColor">
                                <path d="M2.01 21L23 12 2.01 3 2 10l15 2-15 2z" />
                            </svg>
                        </button>
                    </form>
                </div>
            </div>

            {showProfile && (
                <Profile onClose={() => setShowProfile(false)} />
            )}

            {showSearch && (
                <Search
                    onClose={() => setShowSearch(false)}
                    onSelectUser={handleUserSelect}
                />
            )}
        </div>
    );
};

export default Chat;
