import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import toast from 'react-hot-toast';
import './Auth.css';

const Register = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [usernameError, setUsernameError] = useState('');
    const [passwordError, setPasswordError] = useState('');
    const { register: registerUser, loading, error, isAuthenticated, clearError } = useAuth();
    const navigate = useNavigate();

    useEffect(() => {
        if (isAuthenticated) {
            navigate('/chat');
        }
    }, [isAuthenticated, navigate]);

    useEffect(() => {
        if (error) {
            toast.error(error);
            clearError();
        }
    }, [error, clearError]);

    const onSubmit = async (e) => {
        e.preventDefault();

        // Validate username
        if (!username.trim()) {
            setUsernameError('Username majburiy');
            return;
        }
        if (username.length < 3) {
            setUsernameError('Username kamida 3 ta belgidan iborat bo\'lishi kerak');
            return;
        }

        // Validate password
        if (!password.trim()) {
            setPasswordError('Parol majburiy');
            return;
        }
        if (password.length < 3) {
            setPasswordError('Parol kamida 3 ta belgidan iborat bo\'lishi kerak');
            return;
        }

        setUsernameError('');
        setPasswordError('');

        const result = await registerUser({
            username: username.trim(),
            password: password
        });

        if (result.success) {
            toast.success('Muvaffaqiyatli ro\'yxatdan o\'tdingiz!');
            navigate('/chat');
        }
    };

    const formatPhone = (value) => {
        // Remove all non-digits
        const digits = value.replace(/\D/g, '');

        // Start with +998
        let formatted = '+998';

        if (digits.length > 3) {
            const remaining = digits.slice(3);

            if (remaining.length > 0) {
                formatted += ' ' + remaining.slice(0, 2);
            }
            if (remaining.length > 2) {
                formatted += ' ' + remaining.slice(2, 5);
            }
            if (remaining.length > 5) {
                formatted += ' ' + remaining.slice(5, 7);
            }
            if (remaining.length > 7) {
                formatted += ' ' + remaining.slice(7, 9);
            }
        }

        return formatted;
    };

    const handleUsernameChange = (e) => {
        setUsername(e.target.value);
        setUsernameError('');
    };

    const handlePhoneChange = (e) => {
        let value = e.target.value;

        // If user tries to delete +998, restore it
        if (!value.startsWith('+998')) {
            value = '+998' + value.replace(/^\+?998?/, '');
        }

        const formatted = formatPhone(value);
        setPhone(formatted);
        setPhoneError('');
    };

    return (
        <div className="auth-container">
            <div className="auth-background">
                <div className="auth-card">
                    <div className="auth-header">
                        <div className="auth-logo">
                            <div className="logo-icon">
                                {/* Telegram Icon */}
                                <svg viewBox="0 0 24 24" fill="currentColor">
                                    <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm4.64 6.8c-.15 1.58-.8 5.42-1.13 7.19-.14.75-.42 1-.68 1.03-.58.05-1.02-.38-1.58-.75-.88-.58-1.38-.94-2.23-1.5-.99-.65-.35-1.01.22-1.59.15-.15 2.71-2.48 2.76-2.69.01-.03.01-.14-.07-.2-.08-.06-.19-.04-.27-.02-.11.02-1.93 1.23-5.45 3.61-.52.36-.99.53-1.42.52-.47-.01-1.37-.26-2.03-.48-.82-.27-1.47-.42-1.42-.88.03-.24.37-.49 1.02-.74 4.02-1.76 6.7-2.92 8.05-3.49 3.83-1.6 4.63-1.88 5.15-1.89.11 0 .37.03.54.17.14.12.18.28.2.39.02.11.04.35.02.54z" />
                                </svg>
                            </div>
                            <h1>Telegram Clone</h1>
                        </div>
                        <p className="auth-subtitle">Hisob yaratish uchun ma'lumotlaringizni kiriting</p>
                    </div>

                    <form className="auth-form" onSubmit={onSubmit}>
                        <div className="form-group">
                            <label htmlFor="username">Username</label>
                            <div className="input-wrapper">
                                <input
                                    type="text"
                                    id="username"
                                    className={`form-input ${usernameError ? 'error' : ''}`}
                                    placeholder="Username tanlang"
                                    value={username}
                                    onChange={handleUsernameChange}
                                />
                                <div className="input-icon">
                                    <svg viewBox="0 0 24 24" fill="currentColor">
                                        <path d="M12 12c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm0 2c-2.67 0-8 1.34-8 4v2h16v-2c0-2.66-5.33-4-8-4z" />
                                    </svg>
                                </div>
                            </div>
                            {usernameError && <span className="error-message">{usernameError}</span>}
                        </div>

                        <div className="form-group">
                            <label htmlFor="email">Email</label>
                            <div className="input-wrapper">
                                <input
                                    type="email"
                                    id="email"
                                    className={`form-input ${emailError ? 'error' : ''}`}
                                    placeholder="example@email.com"
                                    value={email}
                                    onChange={(e) => {
                                        setEmail(e.target.value);
                                        setEmailError('');
                                    }}
                                />
                                <div className="input-icon">
                                    <svg viewBox="0 0 24 24" fill="currentColor">
                                        <path d="M20 4H4c-1.1 0-1.99.9-1.99 2L2 18c0 1.1.9 2 2 2h16c1.1 0 2-.9 2-2V6c0-1.1-.9-2-2-2zm0 4l-8 5-8-5V6l8 5 8-5v2z" />
                                    </svg>
                                </div>
                            </div>
                            {emailError && <span className="error-message">{emailError}</span>}
                        </div>

                        <div className="form-group">
                            <label htmlFor="password">Parol</label>
                            <div className="input-wrapper">
                                <input
                                    type="password"
                                    id="password"
                                    className={`form-input ${passwordError ? 'error' : ''}`}
                                    placeholder="Parol yarating"
                                    value={password}
                                    onChange={(e) => {
                                        setPassword(e.target.value);
                                        setPasswordError('');
                                    }}
                                />
                                <div className="input-icon">
                                    <svg viewBox="0 0 24 24" fill="currentColor">
                                        <path d="M18 8h-1V6c0-2.76-2.24-5-5-5S7 3.24 7 6v2H6c-1.1 0-2 .9-2 2v10c0 1.1.9 2 2 2h12c1.1 0 2-.9 2-2V10c0-1.1-.9-2-2-2zm-6 9c-1.1 0-2-.9-2-2s.9-2 2-2 2 .9 2 2-.9 2-2 2zm3.1-9H8.9V6c0-1.71 1.39-3.1 3.1-3.1 1.71 0 3.1 1.39 3.1 3.1v2z" />
                                    </svg>
                                </div>
                            </div>
                            {passwordError && <span className="error-message">{passwordError}</span>}
                        </div>

                        <div className="form-group">
                            <label htmlFor="phone">Telefon Raqami (ixtiyoriy)</label>
                            <div className="input-wrapper">
                                <input
                                    type="text"
                                    id="phone"
                                    className={`form-input ${phoneError ? 'error' : ''}`}
                                    placeholder="+998 XX XXX XX XX (ixtiyoriy)"
                                    value={phone}
                                    onChange={handlePhoneChange}
                                    maxLength={17}
                                />
                                <div className="input-icon">
                                    <svg viewBox="0 0 24 24" fill="currentColor">
                                        <path d="M6.62 10.79c1.44 2.83 3.76 5.14 6.59 6.59l2.2-2.2c.27-.27.67-.36 1.02-.24 1.12.37 2.33.57 3.57.57.55 0 1 .45 1 1V20c0 .55-.45 1-1 1-9.39 0-17-7.61-17-17 0-.55.45-1 1-1h3.5c.55 0 1 .45 1 1 0 1.25.2 2.45.57 3.57.11.35.03.74-.25 1.02l-2.2 2.2z" />
                                    </svg>
                                </div>
                            </div>
                            {phoneError && <span className="error-message">{phoneError}</span>}
                        </div>

                        <div className="phone-info">
                            <p>Majburiy maydonlar: Username, Email, Parol</p>
                        </div>

                        <button
                            type="submit"
                            className={`auth-button ${loading ? 'loading' : ''}`}
                            disabled={loading}
                        >
                            {loading ? (
                                <>
                                    <div className="spinner"></div>
                                    Yaratilmoqda...
                                </>
                            ) : (
                                'Hisob Yaratish'
                            )}
                        </button>
                    </form>

                    <div className="auth-footer">
                        <p>
                            Hisobingiz bormi?{' '}
                            <Link to="/login" className="auth-link">
                                Kirish
                            </Link>
                        </p>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Register;
