import React, { useState, useEffect } from 'react';
import axios from 'axios';
import toast from 'react-hot-toast';
import './CreateGroup.css';

const CreateGroup = ({ onClose, onGroupCreated }) => {
    const [currentStep, setCurrentStep] = useState(1); // Step tracker
    const [groupName, setGroupName] = useState('');
    const [groupDescription, setGroupDescription] = useState('');
    const [selectedMembers, setSelectedMembers] = useState([]);
    const [availableUsers, setAvailableUsers] = useState([]);
    const [loading, setLoading] = useState(false);
    const [creating, setCreating] = useState(false);
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedIcon, setSelectedIcon] = useState('ðŸ‘¥'); // Default group icon

    // Guruh iconlari
    const groupIcons = [
        'ðŸ‘¥', 'ðŸ’¬', 'ðŸŽ¯', 'ðŸš€', 'ðŸ’¡', 'ðŸŽ¨', 'ðŸ“š', 'ðŸŽµ',
        'âš½', 'ðŸŽ®', 'ðŸ’»', 'ðŸ“±', 'ðŸ•', 'â˜•', 'ðŸŒŸ', 'ðŸ”¥',
        'ðŸ’Ž', 'ðŸŽª', 'ðŸŽ­', 'ðŸŽ¨', 'ðŸŽ¯', 'ðŸŽ²', 'ðŸŽ¸', 'ðŸŽ¤'
    ];

    const totalSteps = 3;

    useEffect(() => {
        loadAvailableUsers();
    }, []);

    const loadAvailableUsers = async () => {
        try {
            setLoading(true);
            const token = localStorage.getItem('token');
            const response = await axios.get('http://localhost:5005/api/users/all', {
                headers: { Authorization: `Bearer ${token}` }
            });

            if (response.data.success) {
                setAvailableUsers(response.data.users);
            }
        } catch (error) {
            console.error('Failed to load users:', error);
            toast.error('Foydalanuvchilarni yuklashda xato');
        } finally {
            setLoading(false);
        }
    };

    const handleMemberToggle = (userId) => {
        setSelectedMembers(prev => {
            if (prev.includes(userId)) {
                return prev.filter(id => id !== userId);
            } else {
                return [...prev, userId];
            }
        });
    };

    const handleCreateGroup = async (e) => {
        e.preventDefault();

        if (!groupName.trim()) {
            toast.error('Guruh nomi kerak');
            return;
        }

        if (groupName.trim().length > 50) {
            toast.error('Guruh nomi 50 belgidan oshmasligi kerak');
            return;
        }

        try {
            setCreating(true);
            const token = localStorage.getItem('token');
            const response = await axios.post('http://localhost:5005/api/groups', {
                name: groupName.trim(),
                description: groupDescription.trim(),
                members: selectedMembers,
                isPublic: false,
                icon: selectedIcon // Icon qo'shish
            }, {
                headers: { Authorization: `Bearer ${token}` }
            });

            if (response.data.success) {
                toast.success('Guruh muvaffaqiyatli yaratildi');
                onGroupCreated(response.data.group);
                onClose();
            }
        } catch (error) {
            console.error('Failed to create group:', error);
            const message = error.response?.data?.message || 'Guruh yaratishda xato';
            toast.error(message);
        } finally {
            setCreating(false);
        }
    };

    const filteredUsers = availableUsers.filter(user =>
        user.username.toLowerCase().includes(searchTerm.toLowerCase())
    );

    // Step navigation functions
    const nextStep = () => {
        if (currentStep < totalSteps) {
            setCurrentStep(currentStep + 1);
        }
    };

    const prevStep = () => {
        if (currentStep > 1) {
            setCurrentStep(currentStep - 1);
        }
    };

    const canProceedToStep2 = () => {
        return groupName.trim().length > 0;
    };

    const canProceedToStep3 = () => {
        return selectedMembers.length > 0;
    };

    // Step titles
    const getStepTitle = () => {
        switch (currentStep) {
            case 1: return 'Guruh Ma\'lumotlari';
            case 2: return 'A\'zolarni Tanlash';
            case 3: return 'Tasdiqlash';
            default: return 'Yangi Guruh';
        }
    };

    return (
        <div className="create-group-overlay">
            <div className="create-group-modal">
                <div className="create-group-header">
                    <h2>Yangi Guruh Yaratish</h2>
                    <button className="close-button" onClick={onClose}>
                        <svg viewBox="0 0 24 24" fill="currentColor">
                            <path d="M19 6.41L17.59 5 12 10.59 6.41 5 5 6.41 10.59 12 5 17.59 6.41 19 12 13.41 17.59 19 19 17.59 13.41 12z" />
                        </svg>
                    </button>
                </div>

                <form className="create-group-form" onSubmit={handleCreateGroup}>
                    <div className="form-section">
                        <h3>Guruh Ma'lumotlari</h3>

                        <div className="form-group">
                            <label>Guruh Iconi</label>
                            <div className="icon-selector">
                                <div className="selected-icon-display">
                                    <span className="selected-icon">{selectedIcon}</span>
                                </div>
                                <div className="icon-grid">
                                    {groupIcons.map(icon => (
                                        <button
                                            key={icon}
                                            type="button"
                                            className={`icon-option ${selectedIcon === icon ? 'selected' : ''}`}
                                            onClick={() => setSelectedIcon(icon)}
                                        >
                                            {icon}
                                        </button>
                                    ))}
                                </div>
                            </div>
                        </div>

                        <div className="form-group">
                            <label htmlFor="groupName">Guruh Nomi *</label>
                            <input
                                type="text"
                                id="groupName"
                                value={groupName}
                                onChange={(e) => setGroupName(e.target.value)}
                                placeholder="Guruh nomini kiriting..."
                                maxLength={50}
                                required
                            />
                            <span className="char-count">{groupName.length}/50</span>
                        </div>

                        <div className="form-group">
                            <label htmlFor="groupDescription">Tavsif (ixtiyoriy)</label>
                            <textarea
                                id="groupDescription"
                                value={groupDescription}
                                onChange={(e) => setGroupDescription(e.target.value)}
                                placeholder="Guruh haqida qisqacha ma'lumot..."
                                maxLength={200}
                                rows={3}
                            />
                            <span className="char-count">{groupDescription.length}/200</span>
                        </div>
                    </div>

                    <div className="form-section">
                        <h3>A'zolarni Tanlash</h3>

                        <div className="search-members">
                            <input
                                type="text"
                                placeholder="Foydalanuvchilarni qidirish..."
                                value={searchTerm}
                                onChange={(e) => setSearchTerm(e.target.value)}
                                className="member-search-input"
                            />
                        </div>

                        {selectedMembers.length > 0 && (
                            <div className="selected-members">
                                <h4>Tanlangan a'zolar ({selectedMembers.length})</h4>
                                <div className="selected-members-list">
                                    {selectedMembers.map(memberId => {
                                        const user = availableUsers.find(u => u.id === memberId);
                                        return user ? (
                                            <div key={memberId} className="selected-member">
                                                <img src={user.avatar} alt={user.username} />
                                                <span>{user.username}</span>
                                                <button
                                                    type="button"
                                                    onClick={() => handleMemberToggle(memberId)}
                                                    className="remove-member"
                                                >
                                                    <svg viewBox="0 0 24 24" fill="currentColor">
                                                        <path d="M19 6.41L17.59 5 12 10.59 6.41 5 5 6.41 10.59 12 5 17.59 6.41 19 12 13.41 17.59 19 19 17.59 13.41 12z" />
                                                    </svg>
                                                </button>
                                            </div>
                                        ) : null;
                                    })}
                                </div>
                            </div>
                        )}

                        <div className="available-members">
                            {loading ? (
                                <div className="loading-members">
                                    <div className="spinner"></div>
                                    <p>Foydalanuvchilar yuklanmoqda...</p>
                                </div>
                            ) : (
                                <div className="members-list">
                                    {filteredUsers.length === 0 ? (
                                        <div className="no-users">
                                            <p>Foydalanuvchilar topilmadi</p>
                                        </div>
                                    ) : (
                                        filteredUsers.map(user => (
                                            <div
                                                key={user.id}
                                                className={`member-item ${selectedMembers.includes(user.id) ? 'selected' : ''}`}
                                                onClick={() => handleMemberToggle(user.id)}
                                            >
                                                <div className="member-avatar-container">
                                                    <img src={user.avatar} alt={user.username} />
                                                </div>
                                                <div className="member-info">
                                                    <div className="member-name">{user.username}</div>
                                                    <div className={`member-status ${user.isOnline ? 'online' : 'offline'}`}>
                                                        {user.isOnline ? 'Online' : 'Offline'}
                                                    </div>
                                                </div>
                                                <div className="member-checkbox">
                                                    <input
                                                        type="checkbox"
                                                        checked={selectedMembers.includes(user.id)}
                                                        onChange={() => handleMemberToggle(user.id)}
                                                    />
                                                </div>
                                            </div>
                                        ))
                                    )}
                                </div>
                            )}
                        </div>
                    </div>

                    <div className="form-footer">
                        <button type="button" className="cancel-button" onClick={onClose}>
                            Bekor qilish
                        </button>
                        <button
                            type="submit"
                            className="create-button"
                            disabled={creating || !groupName.trim()}
                        >
                            {creating ? (
                                <>
                                    <div className="spinner-small"></div>
                                    Yaratilmoqda...
                                </>
                            ) : (
                                'Guruh Yaratish'
                            )}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default CreateGroup;
